(482, 2251, '_aviaLayoutBuilderCleanData', '[av_fullscreen size=''no scaling'' animation=''fade'' autoplay=''true'' interval=''3'' control_layout=''av-control-hidden'' scroll_down=''aviaTBscroll_down'' perma_caption=''aviaTBperma_caption'']\r\n[av_fullscreen_slide slide_type=''image'' id=''3255'' position=''top center'' video=''http://'' mobile_image='''' video_cover='''' title=''Never Forget The Special Dates'' custom_title_size=''36'' custom_content_size=''20'' caption_pos=''caption_right'' link_apply='''' link=''lightbox'' link_target='''' button_label='''' button_color=''light'' link1=''manually,http://'' link_target1='''' button_label2=''Click me'' button_color2=''light'' link2=''manually,http://'' link_target2='''' font_color='''' custom_title='''' custom_content='''' overlay_enable=''aviaTBaviaTBoverlay_enable'' overlay_opacity=''0.5'' overlay_color=''#000000'' overlay_pattern='''' overlay_custom_pattern='''' video_controls='''' video_mute='''' video_loop='''' video_autoplay='''']\r\nCreate Birthday, Anniversary and Occasional Reminders\r\n[/av_fullscreen_slide]\r\n[av_fullscreen_slide slide_type=''image'' id=''3254'' position=''top center'' video='''' mobile_image='''' video_cover='''' title='''' custom_title_size='''' custom_content_size='''' caption_pos=''caption_right caption_right_framed caption_framed'' link_apply='''' link=''lightbox'' link_target='''' button_label='''' button_color=''light'' link1=''manually,http://'' link_target1='''' button_label2='''' button_color2=''light'' link2=''manually,http://'' link_target2='''' font_color='''' custom_title='''' custom_content='''' overlay_enable=''aviaTBaviaTBoverlay_enable'' overlay_opacity=''0.5'' overlay_color=''#000000'' overlay_pattern='''' overlay_custom_pattern='''' video_controls='''' video_mute='''' video_loop='''' video_autoplay=''''][/av_fullscreen_slide]\r\n[/av_fullscreen]\r\n\r\n[av_section color=''alternate_color'' custom_bg='''' src='''' attachment='''' position=''top left'' repeat=''repeat'' attach=''scroll'' padding=''large'' shadow=''shadow'' id=''portfolio'']\r\n\r\n[av_one_full first]\r\n\r\n[/av_one_full][av_one_full first]\r\n\r\n[av_textblock size='''' font_color='''' color='''']\r\n<h2 style="text-align: center; text-transform: none;">ReemindMe - Reminder Solution for Corporate & Individuals</h2>\r\n[/av_textblock]\r\n\r\n[/av_one_full][av_one_half first]\r\n\r\n[av_textblock size='''' font_color='''' color='''']\r\n<h2 style="text-align: center; text-transform: none;">Individuals</h2>\r\n[/av_textblock]\r\n\r\n[av_textblock size='''' font_color='''' color='''']\r\n<ul>\r\n	<li>Remember special occasions with anniversary or birthday reminders</li>\r\n	<li>Remind yourself & others about appointments, renewals & things to-do</li>\r\n	<li>be reminded anywhere with text message reminders & <strong>email reminders</strong></li>\r\n	<li>Plan ahead or manage tasks using a <strong>reminder calendar</strong> & day planners</li>\r\n</ul>\r\n[/av_textblock]\r\n\r\n[/av_one_half][av_one_half]\r\n\r\n[av_textblock size='''' font_color='''' color='''']\r\n<h2 style="text-align: center; text-transform: none;"> Corporates</h2>\r\n[/av_textblock]\r\n\r\n[av_textblock size='''' font_color='''' color='''']\r\n<ul>\r\n	<li>Import Employee contacts</li>\r\n	<li>Set Birthday, Anniversary or other reminders per employee</li>\r\n	<li>Add Employee''s Spouse and Child''s Birthday and make them feel special</li>\r\n	<li>Custom Sender ID - DM-YOURCOMPNAY</li>\r\n</ul>\r\n[/av_textblock]\r\n\r\n[/av_one_half][av_hr class=''invisible'' height=''30'' shadow=''no-shadow'' position=''center'']\r\n\r\n[av_hr class=''invisible'' height=''30'' shadow=''no-shadow'' position=''center'']\r\n\r\n[/av_section][av_section min_height='''' min_height_px=''500px'' padding=''large'' shadow=''shadow'' bottom_border=''no-border-styling'' id='''' color=''alternate_color'' custom_bg='''' src='''' attachment='''' attachment_size='''' attach=''scroll'' position=''top left'' repeat=''repeat'' video='''' video_ratio=''16:9'' overlay_opacity=''0.5'' overlay_color='''' overlay_pattern='''' overlay_custom_pattern='''']\r\n[av_button label=''About'' link=''manually,#about-us'' link_target='''' color=''theme-color-subtle'' custom_bg=''#444444'' custom_font=''#ffffff'' size=''large'' position=''center'' icon_select=''yes'' icon=''188'']\r\n[/av_section]\r\n\r\n[av_section color=''main_color'' custom_bg='''' src=''http://farm3.staticflickr.com/2863/12422138414_801bdd460d_b.jpg'' attachment='''' position=''top left'' repeat=''stretch'' attach=''fixed'' padding=''large'' shadow=''no-shadow'' id='''']\r\n[av_hr class=''invisible'' height=''100'' shadow=''no-shadow'' position=''center'']\r\n[/av_section]\r\n\r\n[av_section color=''alternate_color'' custom_bg='''' src='''' attachment='''' position=''top left'' repeat=''no-repeat'' attach=''scroll'' padding=''large'' shadow=''shadow'' id=''about-us'']\r\n[av_one_half first]\r\n\r\n[av_textblock size='''' font_color='''' color='''']\r\n<h2 style="text-align: right;">About Us</h2>\r\n[/av_textblock]\r\n\r\n[av_hr class=''short'' height=''70'' position=''right'']\r\n\r\n[av_textblock size='''' font_color='''' color='''']\r\n<p style="text-align: right;">In 2014 the SriTech revolutionized the IT Industry with the ReemindMe.com delivery platform enabling Individuals & Corporates to create and deliver reminders via mobile messaging technology. ReemindMe.com is a mobile messaging technology website focused on providing straight & reliable reminder service.</p>\r\n[/av_textblock]\r\n\r\n[av_hr class=''short'' height=''70'' position=''right'']\r\n\r\n[av_button label=''Check out our Team'' link=''manually,#team'' link_target='''' color=''theme-color-subtle'' custom_bg=''#444444'' custom_font=''#ffffff'' size=''large'' position=''right'' icon_select=''yes'' icon=''14'']\r\n\r\n[/av_one_half][av_one_half]\r\n\r\n[av_contact email=''office@testmail.net'' button=''Submit'' title=''Send us mail'' sent=''Your message has been sent!'' 